mapper.select:
SELECT u.login_name, u.display_name, m.id, m.app_id, m.app_version_id, m.commentbody, m.create_date, m.rank_value*30 as rank_value, a.app_name, p.head_photo_url FROM app_comment m LEFT JOIN sys_user u ON m.user_id = u.id LEFT JOIN app_main a ON m.app_id = a.app_id LEFT JOIN user_head_photo p on u.id = p.user_id AND m.app_version_id = a.app_version AND p.photo_status = '1'
and m.app_id = #{userZoneAppComment.app_id}
and m.app_version_id = #{userZoneAppComment.app_version_id}
and m.status = '1'
order by m.create_date desc

SELECT count(*) FROM app_comment m LEFT JOIN sys_user u ON m.user_id = u.id LEFT JOIN app_main a ON m.app_id = a.app_id AND m.app_version_id = a.app_version
and m.app_id = #{userZoneAppComment.app_id}
and m.app_version_id = #{userZoneAppComment.app_version_id}
and m.status = '1'
order by m.create_date desc
